mapper.update:
UPDATE pf_members 
SET userkey=#{key}
WHERE id=#{user_id}

UPDATE pf_members
SET userkey='Y'
WHERE id=#{user_id} AND userkey=#{key}

mapper.select:
SELECT member_no, type, id, name, email
FROM pf_members
WHERE member_no=#{senderNumber}

SELECT email
FROM pf_members
WHERE email=#{email}

SELECT provider
FROM pf_members
WHERE email=#{email}

SELECT member_no, type, id, name, email
FROM pf_members
WHERE email=#{email} AND provider=#{provider}

mapper.update:
UPDATE pf_members
SET provider=#{provider}, userKey=null
WHERE email=#{email}

UPDATE pf_members
SET name=#{name}, id=#{id}, email=#{email}, tel=#{tel}, sms_yn=#{smsYN}, email_yn=#{emailYN}
WHERE member_no=#{number}

UPDATE pf_members
SET post_no=#{postNumber}, basic_address=#{basicAddress}, detail_address=#{detailAddress}
WHERE member_no=#{number}
