mapper.select:
SELECT p.board_no, p.readable, count(r.board_no) count, p.homepage, p.thumbnail, p.readable, b.board_no, b.title, b.content, b.view_count, b.registered_date, m.member_no, -- 회원번호 -- m.id, -- id값 -- m.name, -- 이름 -- m.email, -- 이메일 -- g.seeking_flag, -- 재직여부 -- m.photo, -- 사진경로 -- g.membership -- 멤버십 -- FROM pf_portfolio as p LEFT OUTER JOIN pf_board as b ON p.board_no = b.board_no LEFT OUTER JOIN pf_general_member as g ON g.general_member_no = p.general_member_no LEFT OUTER JOIN pf_members as m ON m.member_no = g.general_member_no LEFT OUTER JOIN pf_recommendation r ON b.board_no = r.board_no
WHERE m.member_no = #{member.number}
GROUP BY p.board_no limit #{startIndex}, #{pageSize}

SELECT p.board_no, count(r.board_no) count, p.homepage, p.thumbnail, p.readable, b.board_no, b.title, b.content, b.view_count, b.registered_date, m.member_no, -- 회원번호 -- m.id, -- id값 -- m.name, -- 이름 -- m.email, -- 이메일 -- g.seeking_flag, -- 재직여부 -- m.photo, -- 사진경로 -- g.membership -- 멤버십 -- FROM pf_portfolio as p LEFT OUTER JOIN pf_board as b ON p.board_no = b.board_no LEFT OUTER JOIN pf_general_member as g ON g.general_member_no = p.general_member_no LEFT OUTER JOIN pf_members as m ON m.member_no = g.general_member_no LEFT OUTER JOIN pf_recommendation r ON b.board_no = r.board_no
WHERE p.board_no = #{number}
GROUP BY p.board_no

mapper.update:
UPDATE pf_portfolio

 homepage = #{homepage}, 
 thumbnail = #{thumbnail}, 
 readable = #{readable} 

WHERE board_no = #{number}
